function initialize(){var o={center:myCenter,zoom:14,scrollwheel:!1,mapTypeControlOptions:{mapTypeIds:[google.maps.MapTypeId.ROADMAP,"map_style"]}},e=new google.maps.Map(document.getElementById("googleMap"),o),a=new google.maps.Marker({position:myMaker,icon:$obj.data("icon"),animation:google.maps.Animation.DROP});a.setMap(e);var n=new google.maps.InfoWindow;n.setContent("<p><strong> "+$obj.data("address")+"</strong></p>"),a.addListener("click",function(){n.open(e,a)})}if(jQuery("#googleMap").length>0){$obj=jQuery("#googleMap");var myCenter=new google.maps.LatLng($obj.data("lat"),$obj.data("lon")),myMaker=new google.maps.LatLng($obj.data("lat"),$obj.data("lon"));google.maps.event.addDomListener(window,"load",initialize)}